import React, { Component } from "react";
import "/Users/nora.luo/Desktop/react/0330/exer/demo02/src/assets/css/Footer.css";
import footerData from "../../assets/footer/footerData";
class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: null,
        };
    }
    componentDidMount() {
        this.setState({
            list: footerData
        })
    }
    userBtn(index) {
        footerData.forEach((val, inx) => {
            val.addClass = false;
            if (inx === index) {
                val.addClass = true;
            }
        });
        this.setState({
            list: footerData
        })
    }
    render() {
        return (
            <div className="fixedfooter">
                <ul>
                    {
                        this.state.list.map((value, index) => {
                            return (
                                <li key={index} onClick={this.userBtn.bind(this, index)}>
                                    <img src={value.addClass ? value.ckimage : value.moimage} alt="" /><br />
                                    <span className={value.addClass ? 'footerColor' : ''}>{value.title}</span>
                                </li>
                            )
                        })
                    }
                </ul>
            </div>
        );
    }
}

export default Header;
